import React, { useEffect, useRef, useState } from 'react';
import './App.css';
import { PatientVisualizer } from 'fhir-visualizers';
import { useForm } from 'react-hook-form';
import { Spinner, Container, Button, Form } from 'react-bootstrap';

export default function App(props) {
  const client = props.client;
  const [patient, setPatient] = useState(null);
  const lastReport = useRef(null);

  useEffect(() => {
    client.patient.read().then((patient) => setPatient(patient));
    client.patient
      .request('DiagnosticReport')
      .then((response) => {
        let studies = response.entry.map((entry) => entry.resource);
        lastReport.current = studies;
      });
  });

  // Function to handle file upload
  const handleFileUpload = (selectedFile) => {
    const formdata = new FormData();
    formdata.append('input_file', selectedFile, '1_IM-0001-4002.dcm');

    const requestOptions = {
      method: 'POST',
      body: formdata,
      redirect: 'follow',
    };

    fetch('http://127.0.0.1:80/dicom_to_report', requestOptions)
      .then((response) => response.text())
      .then((result) => console.log(result))
      .catch((error) => console.log('error', error));
  };

  // Rest of your code...

  return (
    <div id="app">
      <Container>
        {pat}
      </Container>
      {patient ? (
        <Container>
          <h4>Update Patient Name</h4>
          <Form onSubmit={handleSubmit(onSubmit)}>
            <Form.Group className="mb-3" controlId="formFirst">
              <Form.Label>First</Form.Label>
              <Form.Control
                type="text"
                {...register('fname')}
                defaultValue={patient.name[0].given.join(' ')}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formLast">
              <Form.Label>Last</Form.Label>
              <Form.Control
                type="text"
                {...register('lname')}
                defaultValue={patient.name[0].family}
              />
            </Form.Group>
            <Button type="submit">Update</Button>
          </Form>
        </Container>
      ) : (
        ''
      )}

      <Container>
        <input
          type="file"
          id="fileInput"
          onChange={(e) => handleFileUpload(e.target.files[0])}
          accept=".dcm"
        />
      </Container>

      <Container>
        <br />
        <br />
        <h4>Fetch API Report</h4>
        <br />
        <h4>Existing Report</h4>
        {lastReport.current != null ? (
          lastReport.current.map((report, index) => (
            <div key={index}>
              <h5>Report {index + 1}</h5>
              <pre>{JSON.stringify(report, null, 2)}</pre>
            </div>
          ))
        ) : (
          <p>No existing reports available.</p>
        )}
      </Container>
    </div>
  );
}
