import React,{useEffect,useRef,useState} from 'react';
import './App.css';
import {PatientVisualizer} from 'fhir-visualizers';
import { useForm } from "react-hook-form";
import {Spinner, Container, Button, Form} from "react-bootstrap";
// import CsvDownloadButton from './CsvDownloadButton';

export default function App(props) {
  const client=props.client;
  const [patient, setPatient] = useState(null);
  const lastReport = useRef(null);

  useEffect(() => {
    client.patient.read().then((patient) => setPatient(patient));
    // client.request(`DiagnosticReport?patient=${client.patient.id}`)
    client.patient.request("DiagnosticReport")
    .then(response => {
      let studies = response.entry.map(entry => entry.resource);
      lastReport.current = studies;
    })
    if (lastReport.current != null) {
      // console.log(JSON.stringify(lastReport.current[0]));
    }
  });

  let pat=patient?
  <PatientVisualizer patient={patient} />
  : <Spinner/>;

  const { register, handleSubmit, formState: { errors } } = useForm();
  const onSubmit = data => {
    let np=patient;
    np.name[0].given[0]=data.fname;
    np.name[0].family=data.lname;
    setPatient(np);
    client.update(np);
  };

  return (
    <div id="app">
      <Container>
        {pat}
      </Container>
      {patient?
      <Container>
        <h4>Update Patient Name</h4>
      <Form onSubmit={handleSubmit(onSubmit)}>
        <Form.Group className="mb-3" controlId="formFirst">
        <Form.Label>First</Form.Label>
        <Form.Control type="text" {...register("fname")} defaultValue={ patient.name[0].given.join(' ')} />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formLast">
        <Form.Label>Last</Form.Label>
        <Form.Control type="text" {...register("lname")} defaultValue={ patient.name[0].family } />
        </Form.Group>
        <Button type="submit" >Update</Button>
      </Form>
      </Container>
      :""}
      {/* <Container>
        <Button type="button" className="btn btn-primary" onClick={getStudies}>Get Report</Button>
      </Container> */}
      {/* <Container>
        <br/>
        <CsvDownloadButton data={
          lastReport.current != null ?
          lastReport.current.map(report =>
            report
          )
          :[]

        } />
        <br/>
        <h4>Existing Report</h4>
        {lastReport.current != null ?
        lastReport.current.map(report =>
          <pre>{JSON.stringify(report, null, 2)}</pre>)
        :""}


      </Container> */}
      <Container>
  <br />
  <br />
  raga
  <br />
  <h4>Existing Report</h4>
  {lastReport.current != null ? (
    lastReport.current.map((report, index) => (
      <div key={index}>
        <h5>Report {index + 1}</h5>
        <pre>{JSON.stringify(report, null, 2)}</pre>
      </div>
    ))
  ) : (
    <p>No existing reports available.</p>
  )}
</Container>

    </div>
  );
};
